import React, {useState, useEffect} from 'react';
import Inspirations from "./components/Inspirations/Inspirations"; 
import NewInspiration from "./components/NewInspiration/NewInspiration";
import Axios from 'axios';

const DUMMY_EXPENSES = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const [inspirationList, setInspirationList] = useState([]);
useEffect(()=>{
  Axios.get("http://localhost:3001/api/get").then((response)=>{
      setInspirationList(response.data);
  });
},[]);

function App() {
  const [inspirations, setInspirations] = useState(DUMMY_EXPENSES);

  function addInspirationHandler(inspirationData) {
      setInspirations((prevInspirations) => {
        return [inspirationData, ...prevInspirations];
      });
  }

  return (
    <div>
      <NewInspiration onAddInspiration={addInspirationHandler}/>
      <Inspirations items={inspirations} />
    </div>
  );
}

export default App;
