import React, {useState} from 'react';
import "./ExpenseForm.css";

function ExpenseForm(props) {
    const [enteredTitle, setEnteredTitle] = useState('');
    function titleChangeHandler(event) {
        setEnteredTitle(event.target.value); 
    }

    const [enteredComments, setEnteredComments] = useState('');
    function commentChangeHandler(event) {
        setEnteredComments(event.target.value); 
    }

    const [enteredInspirationURL, setEnteredInspirationURL] = useState('');
    function inspirationURLChangeHandler(event) {
        setEnteredInspirationURL(event.target.value); 
    }

    const [enteredInteractivityTags, setEnteredInteractivityTagsL] = useState('');
    function interactivityTagsChangeHandler(event) {
        setEnteredInteractivityTagsL(event.target.value); 
    }

    const [enteredDate, setEnteredDate] = useState('');
    function dateChangeHandler(event) {
        setEnteredDate(event.target.value); 
    } 

    function submitHandler(event) {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date: new Date(enteredDate)
        };

        props.onSaveExpenseData(expenseData);
        setEnteredTitle("");
        setEnteredAmount("");
        setEnteredDate("");
    }

    var expanded = false;

    function showCheckboxes() {
    var checkboxes = document.getElementById("checkboxes");
        if (!expanded) {
            checkboxes.style.display = "block";
            expanded = true;
        } else {
            checkboxes.style.display = "none";
            expanded = false;
        }
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={enteredTitle} onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Comments</label>
                    <input type="text" value={enteredComments} onChange={commentChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Interactivity Inspiration Link</label>
                    <input type="url" value={enteredInspirationURL} onChange={inspirationURLChangeHandler}/>
                </div>
                <div className="new-expense__control multiselect">
                    <div className="selectBox" onClick={showCheckboxes}>
                    <select>
                        <option>Select an option</option>
                    </select>
                    <div className="overSelect"></div>
                    </div>
                    <div id="checkboxes">
                    <label for="one">
                        <input type="checkbox" id="one" />First checkbox</label>
                    <label for="two">
                        <input type="checkbox" id="two" />Second checkbox</label>
                    <label for="three">
                        <input type="checkbox" id="three" />Third checkbox</label>
                    </div>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" value={enteredDate} min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="button" onClick={props.onCancel}>Cancel</button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
