import React, {useState} from 'react';
import "./Edit.css";

function Edit(props){
    const [editInspirationTitle, setEditInspirationTitle] = useState('');
    function editInspirationTitleChangeHandler(event) {
        setEditInspirationTitle(event.target.value); 
    }

    const [editInspirationLink, setEditInspirationLink] = useState('');
    function editInspirationLinkChangeHandler(event) {
        setEditInspirationLink(event.target.value); 
    }

    const [editInspirationComments, setEditInspirationComments] = useState('');
    function editInspirationCommentsChangeHandler(event) {
        setEditInspirationComments(event.target.value); 
    }

    var expandedTags = {"inspiration":false, "projects":false};

    function showCheckboxes(tagName) {
        var checkboxId = "checkboxes-"+tagName;
        var checkboxes = document.getElementById(checkboxId);
            if (!expandedTags[tagName]) {
                checkboxes.style.display = "block";
                expandedTags[tagName] = true;
            } else {
                checkboxes.style.display = "none";
                expandedTags[tagName] = false;
            }
    }

    function saveHandler(){

    }

    return (
        <div className="edit-popup">
            <form onSubmit={saveHandler}>
                <div className="new-inspiration__controls">
                    <div className="new-inspiration__control">
                        <input type="text" className="new-inspiration__control_input" placeholder="Title of Inspiration Site" value={props.title} onChange={editInspirationTitleChangeHandler} required/>
                    </div>
                    <div className="new-inspiration__control">
                        <input type="url" className="new-inspiration__control_input" placeholder="Site URL" value={props.link} onChange={editInspirationLinkChangeHandler} required/>
                    </div>
                    <div className="new-inspiration__control">
                        <input type="file" id="myFile" name="filename" accept="image/*"/>
                    </div>
                    <div className="new-inspiration__control">
                        <textarea className="new-inspiration__control_input" rows="4" cols="50" placeholder="Why is this inspiration cool?" value={props.comment} onChange={editInspirationCommentsChangeHandler} required></textarea>
                    </div>
                </div>
                <div className="new-inspiration__actions">
                    <button type="button" onClick={function(){props.onPopupEditHandler(props.id)}}>Save</button>
                    <button type="button" onClick={props.onCancelEditHandler}>Cancel</button>
                </div>
            </form>
        </div>
    )

}

export default Edit;
