import React, {useState} from 'react';
import Card from "../UI/Card";
import InspirationsFilter from "./InspirationsFilter";
import InspirationsList from './InspirationsList';
import "./Inspirations.css";

function Inspirations(props) {
    const [filteredTag, setFilteredTag] = useState('all');

    function filterChangeHandler(selectedTag) {
         setFilteredTag(selectedTag);
    }

    const filteredInspiration = props.items.filter(inspiration => {
        var jsonInspirationTags = JSON.parse(inspiration.inspirationTags);
        var tags = []
        for(var key in jsonInspirationTags) {
            if(jsonInspirationTags[key]==="1") {
                tags.push(key);
            }
        }
        return tags === filteredTag;
    });
    console.log(filteredInspiration);

    return (
        <div>
            <Card className="expenses">
                <InspirationsFilter selected={filteredTag} onFilterChange={filterChangeHandler}/>
                <InspirationsList items={filteredInspiration} />
            </Card>
        </div>
    )
}

export default Inspirations;
