import React, {useState, useEffect} from 'react';
import "./ExpenseForm.css";
import Axios from 'axios';

function ExpenseForm(props) {
    const [inspirationTitle, setInspirationTitle] = useState('');
    function inspirationTitleChangeHandler(event) {
        setInspirationTitle(event.target.value); 
    }

    const [inspirationComments, setInspirationComments] = useState('');
    function inspirationCommentsChangeHandler(event) {
        setInspirationComments(event.target.value); 
    }

    const [inspirationLink, setInspirationLink] = useState('');
    function inspirationLinkChangeHandler(event) {
        setInspirationLink(event.target.value); 
    }

    const [interactivityTags, setInteractivityTags] = useState('');
    function interactivityTagsChangeHandler(event) {
        setInteractivityTags(event.target.value); 
    }

    function submitHandler(event) {
        event.preventDefault();
        Axios.post("https://localhost:3001/api/insert", {
            inspirationTitle: inspirationTitle, 
            inspirationComments: inspirationComments, 
            inspirationLink: inspirationLink
        }).then(()=>{
            alert("success");
        });

        /*const expenseData = {
            title: enteredTitle
        };

        props.onSaveExpenseData(expenseData);
        setEnteredTitle("");*/
    }

    var expanded = false;

    function showCheckboxes() {
    var checkboxes = document.getElementById("checkboxes");
        if (!expanded) {
            checkboxes.style.display = "block";
            expanded = true;
        } else {
            checkboxes.style.display = "none";
            expanded = false;
        }
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={inspirationTitle} onChange={inspirationTitleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Comments</label>
                    <input type="text" value={inspirationComments} onChange={inspirationCommentsChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Interactivity Inspiration Link</label>
                    <input type="url" value={inspirationLink} onChange={inspirationLinkChangeHandler}/>
                </div>
                <div className="multiselect">
                    <div className="selectBox" onClick={showCheckboxes}>
                    <label>Interactivity Inspiration Tags</label>
                    <select>
                        <option>Select an option</option>
                    </select>
                    <div className="overSelect"></div>
                    </div>
                    <div id="checkboxes">
                    <label for="one">
                        <input type="checkbox" id="one" />Leadspace</label>
                    <label for="two">
                        <input type="checkbox" id="two" />Carousel</label>
                    <label for="three">
                        <input type="checkbox" id="three" />Cards</label>
                    </div>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="button" onClick={props.onCancel}>Cancel</button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
