import React, {useState} from 'react';
import Card from "../UI/Card";
import InspirationsFilter from "./InspirationsFilter";
import InspirationsList from './InspirationsList';
import "./Inspirations.css";

function Inspirations(props) {
    const [filteredTag, setFilteredTag] = useState('all');

    function filterChangeHandler(selectedYear) {
         setFilteredYear(selectedYear);
    }

    const filteredExpenses = props.items.filter(expense => {
            return expense.date.getFullYear().toString() === filteredTag;
    });

    return (
        <div>
            <Card className="expenses">
                <InspirationsFilter selected={filteredYear} onFilterChange={filterChangeHandler}/>
                <InspirationsList items={filteredExpenses} />
            </Card>
        </div>
    )
}

export default Inspirations;
