import React, {useState} from 'react';
import "./ExpenseForm.css";

function ExpenseForm(props) {
    const [enteredTitle, setEnteredTitle] = useState('');
    function titleChangeHandler(event) {
        setEnteredTitle(event.target.value); 
    }

    const [enteredAmount, setEnteredAmount] = useState('');
    function amountChangeHandler(event) {
        setEnteredAmount(event.target.value); 
    }

    const [enteredComments, setEnteredComments] = useState('');
    function commentChangeHandler(event) {
        setEnteredAmount(event.target.value); 
    }

    const [enteredInspirationURL, setEnteredInspirationURL] = useState('');
    function inspirationURLChangeHandler(event) {
        setEnteredAmount(event.target.value); 
    }

    const [enteredDate, setEnteredDate] = useState('');
    function dateChangeHandler(event) {
        setEnteredDate(event.target.value); 
    } 

    function submitHandler(event) {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date: new Date(enteredDate)
        };

        props.onSaveExpenseData(expenseData);
        setEnteredTitle("");
        setEnteredAmount("");
        setEnteredDate("");
    }
    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={enteredTitle} onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" value={enteredAmount} min="0.01" step="0.01" onChange={amountChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Comments</label>
                    <input type="text" value={enteredComments} onChange={commentChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Interactivity Inspiration Link</label>
                    <input type="url" onChange={commentChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" value={enteredDate} min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="button" onClick={props.onCancel}>Cancel</button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
}

export default ExpenseForm;
