import Card from "../UI/Card"
import Delete from "../Popup/Delete";
import React, {useState} from 'react';
import "./InspirationItem.css";
import trash from "../../assets/trash_icon.png";
import edit from "../../assets/edit_icon.png";

function InspirationItem(props) {
    var tags = [];
    const parsedTags = JSON.parse(props.tags)
    for(var key in parsedTags) {
        if(parsedTags[key] === '1' && key!=="all") {
            tags.push(<p className="inspiration-tags" key={key}>{key}</p>);
        }
    }
    tags.reverse();

    const [toDelete, setToDelete] = useState(false);

    function deleteHandler() {
        setToDelete(true);
    }

    function cancelDeleteHandler() {
        setToDelete(false);
    }

    function deletePopup(id,title){
        <Delete id={id} title={title}></Delete>
    }

    return (
        <Card className="inspiration-item">
            <div className="inspiration-item__description">
                <img src={trash} alt="trash icon" className="icon" onClick={()=>{deletePopup(props.id, props.title)}}/>
                <img src={edit} alt="edit icon" className="icon" id="edit-icon"/>
                {tags}
                <h2>{props.title}</h2>
                <p className="inspiration-item__comment">{props.comment}</p>
                <button><a href={props.link} target="_blank" rel="noreferrer">VIEW INSPIRATION</a></button>
            </div> 
        </Card>  
    )
}

export default InspirationItem;
